                    <div class="name" :class="heroCampclass[HEROS[hero.id].camp - 1]">{{ HEROS[hero.id].name }}</div>
                    <div class="image" @click="selectHero(camp, index)">
                        <img :src="`/assets/card/${hero.id}_long.png`">
                    </div>
                    <div class="skills">
